(()=>{document.addEventListener("DOMContentLoaded",function(){document.body.addEventListener("click",function(c){let t=c.target.closest(".copy-button");if(!t)return;let e=t.closest(".code-container");if(!e)return;let o=e.querySelector(".highlight");if(!o)return;let n=o.querySelector("code[data-lang]");if(!n)return;let r=n.textContent;navigator.clipboard.writeText(r).then(()=>{t.textContent="\uF05D",setTimeout(()=>{t.textContent="\uF4BB"},2e3)}).catch(d=>{console.error("Failed to copy: ",d),t.textContent="\uF52F",setTimeout(()=>{t.textContent="\uF4BB"},2e3)})})});})();
